import React from "react";
import { BiCheck, BiMinus, BiPlus } from "react-icons/bi";
import { MdDelete } from "react-icons/md";

const CartScreen = () => {
  const [items, setItems] = React.useState([
    {
      name: "Happy Belly Cage Free Brown Eggs, Grade A",
      price: 3.69,
      quantity: 1,
      imgSrc: `data:image/jpeg;base64,`, // Replace with actual image paths
    },
    {
      name: "Borden, 2% Milk, Gallon, 128 oz",
      price: 4.56,
      quantity: 1,
      imgSrc: `data:image/jpeg;base64,`,
    },
    {
      name: "Cut and Peeled Baby Carrots, 16 oz",
      price: 0.99,
      quantity: 1,
      imgSrc: `data:image/jpeg;base64,`,
    },
    {
      name: "Russet Potatoes, 5 lb",
      price: 2.99,
      quantity: 1,
      imgSrc: `data:image/jpeg;base64,`,
    },
    {
      name: "Happy Belly Salted Butter Sticks, 16 oz",
      price: 3.29,
      quantity: 1,
      imgSrc: `data:image/jpeg;base64,`,
    },
    {
      name: "Red Bell Pepper",
      price: 1.39,
      quantity: 1,
      imgSrc: `data:image/jpeg;base64,`,
    },
    {
      name: "Asparagus, 1 Bunch",
      price: 3.08,
      quantity: 1,
      imgSrc: `data:image/jpeg;base64,`,
    },
  ]);

  const discountCode = "SAVE20";
  const discountPercent = 0.2; // 20%
  const discountAmount = (
    items.reduce((acc, item) => acc + item.price, 0) * discountPercent
  ).toFixed(2);
  const shippingCost = 6.99;
  const subtotal = (
    items.reduce((acc, item) => acc + item.price, 0) +
    shippingCost -
    discountAmount
  ).toFixed(2);

  const handleRemoveItem = (index) => {
    setItems((prevItems) => prevItems.filter((_, i) => i !== index));
  };

  return (
    <div className="container mx-auto">
      <div className="flex py-10 gap-5">
        <div className="grow shadow-lg border border-gray-100 p-5 rounded-lg">
          <h2 className="mb-3 font-bold text-2xl text-primary-300">
            Shopping Cart
          </h2>
          <div>
            {items.map((item, index) => (
              <div className="flex mb-5 gap-5" key={index}>
                <div>
                  <img
                    src={item.imgSrc}
                    alt={item.name}
                    className="aspect-square w-12 rounded object-contain"
                  />
                </div>
                <div className="grow">
                  <div className="font-bold">
                    <span>{item.name}</span>
                  </div>
                  <div>
                    <del className="me-3 text-gray-400">₹{item.price}</del>
                    <span className="text-primary-300 font-bold text-xl">
                      ₹{item.price.toFixed(2)}
                    </span>
                  </div>
                </div>
                <div>
                  <div className="flex items-center gap-2">
                    <button className="bg-primary-300 text-white rounded-full">
                      <BiMinus />
                    </button>
                    <input
                      type="text"
                      value={item.quantity}
                      readOnly
                      className="text-center w-[70px] border rounded"
                    />
                    <button className="bg-primary-300 text-white rounded-full">
                      <BiPlus />
                    </button>
                  </div>
                </div>
                <div>₹{(item.price * item.quantity).toFixed(2)}</div>
                <div>
                  <button
                    className="text-gray-400 hover:text-red-500"
                    onClick={() => handleRemoveItem(index)}
                  >
                    <MdDelete />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="bg-white border-gray-100 p-6 rounded-lg shadow-lg">
          <h3 className="text-xl font-semibold mb-4">Order Summary</h3>

          <div className="flex justify-between items-center mb-4">
            <span className="bg-primary-300 text-white px-3 py-1 rounded">
              7 items
            </span>
            <span className="font-semibold text-lg">₹{subtotal}</span>
          </div>

          {/* Divider */}
          <hr className="border-gray-200 my-4" />

          <div className="mb-4">
            <label htmlFor="shipping" className="block mb-1 font-medium">
              Shipping Method:
            </label>
            <select
              id="shipping"
              className="w-full border rounded px-3 py-2 focus:ring-primary-300 focus:border-primary-300"
            >
              <option value="Express Delivery - ₹6.99">
                Express Delivery - ₹6.99
              </option>
            </select>
          </div>

          {/* Divider */}
          <hr className="border-gray-200 my-4" />

          <div className="mb-4">
            <label htmlFor="discount" className="block mb-1 font-medium">
              Discount Code:
            </label>
            <div className="flex gap-2">
              <input
                type="text"
                id="discount"
                placeholder="Enter a code"
                className="border px-3 py-2 rounded w-full focus:ring-primary-300 focus:border-primary-300"
              />
              <button
                className="bg-primary-300 text-white hover:bg-primary-600 px-3 py-2 rounded"
                aria-label="Apply discount"
              >
                <BiCheck />
              </button>
            </div>
          </div>

          {/* Divider */}
          <hr className="border-gray-200 my-4" />

          <div className="text-sm mb-4">
            <p className="flex justify-between">
              <span>Discount ({discountCode}) 20%:</span>
              <span>₹{discountAmount}</span>
            </p>
            <p className="flex justify-between">
              <span>Shipping:</span>
              <span>₹{shippingCost.toFixed(2)}</span>
            </p>
            <p className="flex justify-between font-semibold">
              <span>Subtotal:</span>
              <span>₹{subtotal}</span>
            </p>
          </div>

          {/* Divider */}
          <hr className="border-gray-200 my-4" />

          <button className="bg-primary-300 text-white rounded-lg py-2 w-full hover:bg-primary-600 transition-colors">
            Continue to Checkout
          </button>
        </div>
      </div>
    </div>
  );
};

export default CartScreen;
